CREATE TABLE IF NOT EXISTS `films` (`id` INTEGER NOT NULL auto_increment , `name` VARCHAR(255), `categoriesId` VARCHAR(255), `year` INTEGER, `filmDirector` VARCHAR(255), `filmDescription` VARCHAR(255), `averageRating` FLOAT, `coverImage` VARCHAR(255), PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `actors` (`id` INTEGER NOT NULL auto_increment , `name` VARCHAR(255), `birthYear` INTEGER, `profilePhoto` VARCHAR(255), PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `film_categories` (`id` INTEGER NOT NULL auto_increment , `name` VARCHAR(255), PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `film_reviews` (`id` INTEGER NOT NULL auto_increment , `ownerName` VARCHAR(255), `ratingPoint` INTEGER, `reviewText` VARCHAR(255), `filmId` INTEGER, PRIMARY KEY (`id`), FOREIGN KEY (`filmId`) REFERENCES `films` (`id`) ON DELETE CASCADE ON UPDATE CASCADE) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `actor_items` (`id` INTEGER NOT NULL auto_increment , `actorId` INTEGER, `filmId` INTEGER, UNIQUE `actor_items_filmId_actorId_unique` (`actorId`, `filmId`), PRIMARY KEY (`id`), FOREIGN KEY (`actorId`) REFERENCES `actors` (`id`) ON DELETE CASCADE ON UPDATE CASCADE, FOREIGN KEY (`filmId`) REFERENCES `films` (`id`) ON DELETE CASCADE ON UPDATE CASCADE) ENGINE=InnoDB;
