CREATE TABLE IF NOT EXISTS `films` (`id` INTEGER NOT NULL auto_increment , `name` VARCHAR(255), `categoryId` VARCHAR(255), `year` INTEGER, `filmDirector` VARCHAR(255), `filmDescription` VARCHAR(255), `averageRating` FLOAT, `coverImage` VARCHAR(255), PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `film_categories` (`id` INTEGER NOT NULL auto_increment , `categoryName` VARCHAR(255), PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `film_reviews` (`id` INTEGER NOT NULL auto_increment , `reviewOwnerName` VARCHAR(255), `reviewPoints` INTEGER, `reviewText` VARCHAR(255), `filmId` INTEGER, PRIMARY KEY (`id`), FOREIGN KEY (`filmId`) REFERENCES `films` (`id`) ON DELETE CASCADE ON UPDATE CASCADE) ENGINE=InnoDB;
