CREATE TABLE IF NOT EXISTS `films` (`id` INTEGER NOT NULL auto_increment , `name` VARCHAR(255), `year` INTEGER, `filmDirector` VARCHAR(255), `filmDescription` TEXT, `averageRating` FLOAT, `coverImage` TEXT, `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `actors` (`id` INTEGER NOT NULL auto_increment , `name` VARCHAR(255), `birthYear` INTEGER, `actorBio` TEXT, `profilePhoto` TEXT, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `categories` (`id` INTEGER NOT NULL auto_increment , `name` VARCHAR(255), PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `users` (`id` INTEGER NOT NULL auto_increment , `name` VARCHAR(255), `email` VARCHAR(255), `password` VARCHAR(255), `role` VARCHAR(255), PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `film_reviews` (`id` INTEGER NOT NULL auto_increment , `ownerName` VARCHAR(255), `ratingPoint` INTEGER, `reviewText` TEXT, `filmId` INTEGER, `userId` INTEGER, PRIMARY KEY (`id`), FOREIGN KEY (`filmId`) REFERENCES `films` (`id`) ON DELETE CASCADE ON UPDATE CASCADE, FOREIGN KEY (`userId`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `film_actor_items` (`id` INTEGER NOT NULL auto_increment , `actorId` INTEGER, `filmId` INTEGER, UNIQUE `film_actor_items_filmId_actorId_unique` (`actorId`, `filmId`), PRIMARY KEY (`id`), FOREIGN KEY (`actorId`) REFERENCES `actors` (`id`) ON DELETE CASCADE ON UPDATE CASCADE, FOREIGN KEY (`filmId`) REFERENCES `films` (`id`) ON DELETE CASCADE ON UPDATE CASCADE) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `film_category_items` (`id` INTEGER NOT NULL auto_increment , `filmId` INTEGER, `categoryId` INTEGER, UNIQUE `film_category_items_filmId_categoryId_unique` (`filmId`, `categoryId`), PRIMARY KEY (`id`), FOREIGN KEY (`filmId`) REFERENCES `films` (`id`) ON DELETE CASCADE ON UPDATE CASCADE, FOREIGN KEY (`categoryId`) REFERENCES `categories` (`id`) ON DELETE CASCADE ON UPDATE CASCADE) ENGINE=InnoDB;

